import React, { Component } from 'react';
import Dialog from '@material-ui/core/Dialog';
import AppBar from '@material-ui/core/AppBar';
import MuiThemeProvider from '@material-ui/core/styles/MuiThemeProvider';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

export class FormSecondaryDetails extends Component {
  continue = e => {
    e.preventDefault();
    this.props.nextStep();
  };

  back = e => {
    e.preventDefault();
    this.props.prevStep();
  };

  constructor(props) {
    super(props);
    this.state = { value: "default" };

  }

  handleChange = event => {
    this.setState({ value: event.target.value });
  };

  render() {
    const { values, handleChange } = this.props;
    return (
      <MuiThemeProvider >
        <React.Fragment>
        <Dialog 
            open="true"
            fullWidth="true"
            maxWidth='sm'
          >
            <AppBar title="part 2" />
            {/* <TextField
            // <span role="img" aria-label="sheep">üêë</span>
              placeholder="Enter Your Rating Prefrence"
              label="Rating"
              onChange={handleChange('Rating')}
              defaultValue={values.Rating}
              margin="normal"
              fullWidth="true"
            /> */}
            <form>
              <label>
                Rating
                <select value={this.state.value} onChange={this.handleChange}>
                  <option value="1star">{'\u2b50'}</option>
                  <option value="2star">{'\u2b50'}{'\u2b50'}</option>
                  <option value="3star">{'\u2b50'}{'\u2b50'}{'\u2b50'}</option>
                  <option value="4star">{'\u2b50'}{'\u2b50'}{'\u2b50'}{'\u2b50'}</option>
                  <option value="5star">{'\u2b50'}{'\u2b50'}{'\u2b50'}{'\u2b50'}{'\u2b50'}</option>
                </select>
              </label>
            </form>
            <br />
            <TextField
              placeholder="Enter Your Distance Preference"
              label="Distance"
              onChange={handleChange('Distance')}
              defaultValue={values.Distance}
              margin="normal"
							fullWidth="true"
            />
            <br />

            <Button
              color="secondary"
              variant="contained"
              onClick={this.back}
            >Back</Button>

            <Button
              color="primary"
              variant="contained"
              onClick={this.continue}
            >Continue</Button>
          </Dialog>
        </React.Fragment>
      </MuiThemeProvider>
    );
  }
}

// // Price preference
// const ratingPref = [
//   { title: 'u2b50' },
//   { title: '$$'},
//   { title: '$$$'},
//   { title: '$$$$'},
// ];

export default FormSecondaryDetails;
