import React, { Component } from 'react';
import Dialog from '@material-ui/core/Dialog';
import AppBar from '@material-ui/core/AppBar';
import MuiThemeProvider from '@material-ui/core/styles/MuiThemeProvider';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

export class FormPrimaryDetails extends Component {
  continue = e => {
    e.preventDefault();
    this.props.nextStep();
  };

  constructor(props) {
    super(props);
    this.state = { value: "coconut" };

    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    alert("Your favorite flavor is: " + this.state.value);
    event.preventDefault();
  }

  handleChange = event => {
    this.setState({ value: event.target.value });
  };

  render() {
    const { values, handleChange } = this.props;
    return (
      <MuiThemeProvider >
        <React.Fragment>
          <Dialog 
            open="true"
            fullWidth="true"
            maxWidth='sm'
          >
            <AppBar title="Enter User Details" />
            <TextField
              placeholder="Enter Your Name"
              // label="Name"
              // onChange={handleChange('Name')}
              // defaultValue={values.Name}
              margin="normal"
              fullWidth="true"
            />
              value={this.state.value} onChange={this.handleChange}>
              <option value="italian">Italian</option>
              <option value="indian">Indian</option>
              <option value="chinese">Chinese</option>
              <option value="japanese">Japanese</option>
              <option value="mexican">Mexican</option>
              <option value="greek">Greek</option>
              <option value="american">American</option>
              <option value="french">French</option>
              <option value="asianFusion">Asian fusion</option>
              <option value="mediterranean">Mediterranean</option>
              <option value="thai">Thai</option>
              <input type="submit" value="Submit" />
            <br />
            <TextField
              placeholder="Enter Your Cuisine Preference"
              label="Cuisine"
              onChange={handleChange('Cuisine')}
              defaultValue={values.Cuisine}
              margin="normal"
							fullWidth="true"
            />
            <br />
            <TextField
              placeholder="Enter Your Price Preference"
              label="Price"
              onChange={handleChange('Price')}
              defaultValue={values.Price}
              margin="normal"
							fullWidth="true"
            />
            <br />
            <Button
              color="primary"
              variant="contained"
              onClick={this.continue}
            >Continue</Button>
          </Dialog>
        </React.Fragment>
      </MuiThemeProvider>
    );
  }
}

export default FormPrimaryDetails;
